var paintings=[{title:"Sunrise",path:"painting1.png",colors:["black"]},{title:"Cold Winter",path:"painting2.png",colors:["yellow"]},{title:"Aurora",path:"painting3.jpg",colors:["blue","yellow"]},{title:"Mighty Mountains",path:"painting4.jpg",colors:["green"]}],colors=[{color:"green",id:"check-green",name:"Green"},{color:"yellow",id:"check-yellow",name:"Yellow"},{color:"blue",id:"check-blue",name:"Blue"}],myPaintings=new Vue({el:"#paintings",data:function(){return{paintings:paintings,searchColor:"",colors:colors,checkboxes:{green:!1,yellow:!1,blue:!1},abc:[]}},methods:{filterByColor:function(t){myPaintings.abc=[],myPaintings.paintings=paintings.filter(function(n){for(var i=0;i<Object.keys(n.colors).length;i++)if(n.colors[i].match(t.toLowerCase()))return n.colors[i]})},showAll:function(){myPaintings.paintings=paintings,myPaintings.abc=[]},checkboxClicked:function(){0<myPaintings.abc.length?filterPaintings(myPaintings.abc):myPaintings.paintings=paintings}}});function filterPaintings(o){myPaintings.paintings=paintings.filter(function(n){for(var i=0;i<Object.keys(n.colors).length;i++)for(var t=0;t<o.length;t++)if(n.colors[i].match(o[t].toLowerCase()))return n.colors[i]})}